SELECT
    count(*)
FROM
    books b
LEFT JOIN
    publisher p ON b.publisher_id = p.id AND p.is_deleted = FALSE
LEFT JOIN
    user u ON b.user_id = u.id AND u.is_deleted = FALSE

    b.is_deleted = FALSE

        AND b.title LIKE #{title} 

        AND b.author LIKE #{author} 

        AND b.publisher_id = #{publisherId}


        AND b.user_id = #{userId}

SELECT b.id AS id, b.title AS title, b.title_kana AS title_kana, b.author AS author, p.name AS publisher_name, u.name AS user_name, b.is_deleted AS is_deleted, b.created_at AS created_at, b.updated_at AS updated_at FROM books b LEFT JOIN publisher p ON b.publisher_id = p.id AND p.is_deleted = FALSE LEFT JOIN user u ON b.user_id = u.id AND u.is_deleted = FALSE
b.is_deleted = FALSE
